    def __init__(self,nameSet=4,delim="-"):
        self.NAME_DELIM=delim
        if "3" in str(nameSet):
            self.NAME_BASE_DIGITS = self.NAME_BASE_DIGITS + self.NAME_BASE_DIGITS3
        if "4" in str(nameSet):
            self.NAME_BASE_DIGITS = self.NAME_BASE_DIGITS + self.NAME_BASE_DIGITS4
        if "5" in str(nameSet):
            self.NAME_BASE_DIGITS = self.NAME_BASE_DIGITS + self.NAME_BASE_DIGITS5
        if not self.NAME_BASE_DIGITS:
            raise ValueError("nameSet must be a combination of 3, 4 or 5 only")
        return

    def _convert2nameBase(self, numberIn, to_digits):
        # make an abs integer out of the numberIn
        x = abs(numberIn) 

        # create the result in base 'len(to_digits)'
        outList = list()
        delim=self.NAME_DELIM
        if x == 0:
            outList.append(to_digits[0])
        else:
            while x > 0:
                digit = x % len(to_digits)
                outList.insert(0,to_digits[digit])
                x = int(x // len(to_digits))
        return delim.join(outList)

    def _convert2decimalBase(self, numberIn, from_digits, to_digits):
        # split on dashes
        x = 0
        #print "DBUG: numberIn[{0}]".format(numberIn)
        for digit in numberIn.split("-"):
            #print "DBUG: digit[{0}]".format(digit)
            try:
                x = x * len(from_digits) + from_digits.index(digit)
            except ValueError:
                raise ValueError('invalid name "%s"' % digit)

        # create the result in base 'len(to_digits)'
        if x == 0:
            res = to_digits[0]
        else:
            res = ''
            while x > 0:
                digit = x % len(to_digits)
                res = to_digits[digit] + res
                x = int(x // len(to_digits))
        return res

    def encode(self, numberIn):
        return(self._convert2nameBase(abs(numberIn), self.NAME_BASE_DIGITS))

    def decode(self, nameBaseIn):
        return(self._convert2decimalBase(nameBaseIn, self.NAME_BASE_DIGITS, self.DECIMAL_BASE_DIGITS))


